function deepClone(obj) {
    let cloneObj = {}; //在堆内存中新建一个对象
    for (let key in obj) {
        //遍历参数的键
        if (typeof obj[key] === 'object') {
            cloneObj[key] = deepClone(obj[key]); //值是对象就再次调用函数
