    for (let i = 0; i  ary[j + 1]) {
                tag = false;
                let temp = ary[j];
                ary[j] = ary[j + 1];
                ary[j + 1] = temp;
                // 结构交换
                // [ary[j], ary[j + 1]] = [ary[j + 1], ary[j]];
            }
        }
        if (tag) {
            break;
        }
    }
    return ary;
}

